,password=#{password}
,user_type=#{userType}
,enabled=#{enabled}
,name=#{name}
,mobile=#{mobile}
,area_id=#{areaId}
,address=#{address}
,zip_code=#{zipCode}
,qq=#{qq}
,id_card=#{idCard}
,account=#{account}
,institution_id=#{institutionId}
,start_date=#{startDate}
,term=#{term}
,auth_type=#{authType}
,update_user=0
,update_time=0
WHERE id=#{id}
